REPLACE INTO `hb_language_locales` (`language_id`,`section`,`keyword`,`value`)
SELECT id, 'global', 'editOrderOwnership','Edit Order Ownership' FROM hb_language WHERE target='admin'
UNION SELECT id, 'global', 'editOrderTakeOwnership','Take Order Ownership' FROM hb_language WHERE target='admin'
UNION SELECT id, 'global', 'lackpriviliges_ownership','You do not have privileges to change ownership' FROM hb_language WHERE target='admin'
UNION SELECT id, 'global', 'hbownershipchange','Hostbill Order Ownership change ' FROM hb_language WHERE target='admin'
UNION SELECT id, 'global', 'hbownershipchangetome','Hostbill Order assigned to me' FROM hb_language WHERE target='admin'
UNION SELECT id, 'global', 'staffownership','Staff ownership' FROM hb_language WHERE target='admin'
UNION SELECT id, 'global', 'takeownership','Take Ownership' FROM hb_language WHERE target='admin';
##########
ALTER TABLE `hb_orders`
ADD COLUMN `staff_member_id` INT NOT NULL DEFAULT '0' AFTER `client_id`;
