REPLACE INTO `hb_reports` (`id`, `type`, `name`, `query`, `options`, `handler`, `state`, `columns`, `displayname`)
SELECT id, 'Invoices', 'MOSS Summary Report', 'SELECT cd.country as `Country code`,\r\n i.taxrate as `VAT rate %`,\r\n COUNT(i.id) as `Invoices`,\r\n sql_currency(SUM(i.subtotal),:currency,0) as `Net amount`,\r\n sql_currency(SUM(i.tax),:currency,0) as `VAT collected` \r\nFROM hb_invoices i \r\nLEFT JOIN hb_client_details cd ON i.client_id = cd.id \r\nWHERE\r\n i.taxrate > 0 \r\nAND i.tax > 0 \r\nAND i.date > :date_bottom \r\nAND i.date < :date_top \r\nAND cd.country IN (\"BE\", \"BG\", \"CZ\", \"DK\", \"DE\", \"EE\", \"IE\", \"EL\", \"ES\", \"FR\", \"HR\", \"IT\", \"CY\", \"LV\", \"LT\", \"LU\", \"HU\", \"MT\", \"NL\", \"AT\", \"PL\", \"PT\", \"RO\", \"SI\", \"SK\", \"FI\", \"SE\", \"UK\", \"GB\") \r\nAND i.currency_id = :currency \r\nGROUP BY cd.country, i.taxrate \r\n\r\nORDER BY cd.country ASC, i.taxrate ASC', 0, 'sql', `state`, `columns`, `displayname` fROM hb_reports WHERE type='Invoices' AND name='MOSS Summary Report';