INSERT INTO `hb_email_templates` (`tplname`, `group`, `for`, `language_id`, `subject`, `message`, `altmessage`, `send`, `plain`, `system`, `hidden`) VALUES
('Order:Pending Review', 'General', 'Admin', 1, 'Order #{$order.id} is pending review', '<h1>Pending review: Order #{$order.id}</h1>\r\n\r\n<h2>Order Info</h2>\r\n<p>\r\n    <b>Order ID:</b> {$order.id}<br>\r\n    <b>Order Number:</b> {$order.number}<br>\r\n    <b>Date Created:</b> {$order.date_created|dateformat:$date_format}<br>\r\n    <b>Invoice Number:</b> {$order.invoice_id}<br>\r\n    <b>Payment Method:</b> {$order.module}<br>\r\n    {if $order.fraudscore}<b>FraudModule risk score:</b> {$order.fraudscore}<br>{/if}\r\n    <b>IP:</b> {$ip_addr}<br>\r\n    <b>Host:</b> {$host_addr}<br>\r\n\r\n</p>\r\n\r\n\r\n<h2>Order Items</h2>\r\n{if $order_details}\r\n<table class="invoice-wrapper" width="100%" cellpadding="0" cellspacing="0">\r\n                      \r\n                      <tr>\r\n                        <td colspan="2">\r\n                          <table class="invoice-table" width="100%" cellpadding="0" cellspacing="0">\r\n                            <tr>\r\n                              <th>\r\n                                <p>Description</p>\r\n                              </th>\r\n                              <th>\r\n                                <p class="align-right">Amount</p>\r\n                              </th>\r\n                            </tr>\r\n                           {foreach from=$order_details item=item}\r\n                            <tr>\r\n                              <td width="80%">{$item.description}</td>\r\n                              <td class="align-right" width="20%">{$item.amount|price:$currency}</td>\r\n                            </tr>\r\n                            {/foreach}\r\n                            <tr>\r\n                              <td width="80%" class="total-cell" valign="middle">\r\n                                <p class="total-cell_label">Total (+tax):</p>\r\n                              </td>\r\n                              <td width="20%" class="total-cell" valign="middle">\r\n                                <p>{$order.total|price:$currency}</p>\r\n                              </td>\r\n                            </tr>\r\n                          </table>\r\n                        </td>\r\n                      </tr>\r\n                    </table>\r\n{else}\r\n    <p>No items to list.</p>\r\n{/if}\r\n   \r\n   \r\n<h2>Client Info</h2>\r\n<p>\r\n    <b>ID:</b> {$client.id} <br>\r\n    <b>Name:</b> {$client.firstname} {$client.lastname} <br>\r\n    <b>Email:</b> {$client.email} <br>\r\n    <b>Company:</b> {$client.companyname} <br>\r\n    <b>Address 1:</b> {$client.address1} <br>\r\n    <b>Address 2:</b> {$client.address2} <br>\r\n    <b>City:</b> {$client.city} <br>\r\n    <b>State/Region:</b> {$client.state} <br>\r\n    <b>Zip Code:</b> {$client.postcode} <br>\r\n    <b>Country:</b> {$client.country} <br>\r\n    <b>Phone Number:</b> {$client.phonenumber} \r\n</p>\r\n', '---------------------------------------------------------------\r\nOrder Info\r\n---------------------------------------------------------------\r\n# Order ID: {$order.id}\r\n# Order Number: {$order.number}\r\n# Date Created: {$order.date_created|dateformat:$date_format}\r\n# Invoice Number: {$order.invoice_id}\r\n# Payment Method: {$order.module}\r\n{if $order.fraudscore}# FraudModule risk score: {$order.fraudscore}{/if}\r\n\r\n---------------------------------------------------------------\r\nClient Info\r\n---------------------------------------------------------------\r\n# ID: {$client.id}\r\n# Name: {$client.firstname} {$client.lastname}\r\n# Email: {$client.email}\r\n# Company: {$client.companyname}\r\n# Address 1: {$client.address1}\r\n# Address 2: {$client.address2}\r\n# City: {$client.city}\r\n# State/Region: {$client.state}\r\n# Zip Code: {$client.postcode}\r\n# Country: {$client.country}\r\n# Phone Number: {$client.phonenumber}\r\n\r\n---------------------------------------------------------------\r\nOrder Items\r\n---------------------------------------------------------------\r\n{if $order_details}\r\n{foreach from=$order_details item=item}\r\n #  {$item.amount|price:$currency} - {$item.description}\r\n{/foreach}\r\n\r\nTotal(+tax): {$order.total|price:$currency}\r\n{else}\r\nNo items to list.\r\n{/if}\r\n\r\n\r\n---------------------------------------------------------------\r\nIP: {$ip_addr}\r\nHost: {$host_addr}', 1, 2, 1, 0);
##########
REPLACE INTO `hb_language_locales` (`language_id`,`section`,`keyword`,`value`)
SELECT id, 'global', 'pendingReviewOrder','Edit Order Ownership' FROM hb_language WHERE target='admin'
UNION SELECT id, 'services', 'BeforePromo', 'Before Promo' FROM hb_language;
