REPLACE INTO hb_configuration (`setting`, `value`) VALUES ('DomainRenewalNotifications', 'single');
INSERT INTO `hb_email_templates` (`tplname`, `group`, `for`, `language_id`, `subject`, `message`, `altmessage`, `send`, `plain`, `system`, `hidden`) VALUES
('Domain:BulkExpires', 'Domain', 'Client', 1, 'Your domains will expire soon', '<h3>Dear {$client.firstname} {$client.lastname}</h3>\r\n\r\n{$domains_count} domain(s) are about to expire in your account with us:<br>\r\n{foreach from=$domains item=domain}\r\n- #{$domain.id} {$domain.name}, expires in {$domain.left}{if $domain.left < 2} day{else} days{/if} (on {$domain.expires|dateformat:$date_format}) - autorenew: {if $domain.autorenew}on{else}off{/if}<br>\r\n{/foreach}\r\n<br>\r\nLogin to our client portal to manage auto-renewal options or renew your domain(s) sooner', 'Dear {$client.firstname} {$client.lastname}\r\n\r\n{$domains_count} domain(s) are about to expire in your account with us:\r\n{foreach from=$domains item=domain}\r\n- #{$domain.id} {$domain.name}, expires in {$domain.left}{if $domain.left < 2} day{else} days{/if} (on {$domain.expires|dateformat:$date_format}) - autorenew: {if $domain.autorenew}on{else}off{/if}\r\n\r\n{/foreach}\r\n\r\nLogin to our client portal to manage auto-renewal options or renew your domain(s) sooner', 1, 2, 1, 0);