INSERT INTO `hb_email_templates` (`tplname`, `group`, `for`, `language_id`, `subject`, `message`, `altmessage`, `send`, `plain`, `system`, `hidden`) VALUES
('Domain:Cancellation Request', 'General', 'Admin', 1, 'Domain #{$details.domain_id} Cancellation Request', '<h1>New cancellation request</h1>\r\n\r\n\r\n<p>\r\n    <b>Client ID:</b> {$client.id}  <br>\r\n    <b>Client Name:</b> {$client.firstname} {$client.lastname}  <br>\r\n</p>\r\n\r\n<p>\r\n    <b>Order ID:</b> {$order_id} <br>\r\n    <b>Domain ID:</b> {$details.domain_id} <br>\r\n    <b>Domain Name:</b> {$domain.name} <br>\r\n    <b>Type:</b> {$details.type} <br>\r\n    <b style="color:red">Reason:</b> {$details.reason} <br> <br>\r\n</p>', 'Cancellation request has been submitted by:\r\n# Client ID: {$client.id} \r\n# Client Name: {$client.firstname} {$client.lastname} \r\n\r\n# Order ID: {$order_id}\r\n\r\n# Domain ID: {$details.domain_id}\r\n# Domain Name: {$domain.name}\r\n# Type: {$details.type}\r\n# Reason: {$details.reason}', 1, 2, 1, 0),
('Domain:Cancellation Request', 'General', 'Client', 1, 'Cancellation Request', '<h1>Dear {$client.firstname} {$client.lastname},</h1>\r\n\r\n<p>This email is a confirmation that we have received your cancellation request</p>\r\n<p>If it was submitted by accident, make sure to contact with us as soon as possible to prevent service interruption</p>\r\n\r\n<p>\r\n    <b>Domain ID:</b> {$details.domain_id}<br>\r\n    <b>Domain Name:</b> {$domain.name}<br>\r\n    <b>Cancelation Type:</b> {$details.type}<br>\r\n    <b>Reason:</b> {$details.reason}\r\n</p>', 'Dear: {$client.firstname} {$client.lastname} \r\n\r\n# We have received following cancelation request: \r\n\r\n# Domain ID: {$details.domain_id}\r\n# Domain Name: {$domain.name}\r\n# Type: {$details.type}\r\n# Reason: {$details.reason}', 1, 2, 1, 0);