REPLACE INTO `hb_language_locales` (`language_id`,`section`,`keyword`,`value`)
INSERT INTO `hb_email_templates` (`tplname`, `group`, `for`, `language_id`, `subject`, `message`, `altmessage`, `send`, `plain`, `system`, `hidden`) VALUES
('Account:ChangePackage:Success', 'Product', 'Client', 1, 'Changing the package for the service', 'Dear {$client.firstname} {$client.lastname},<br>\r\n\r\n<p>Service: {if !empty($service.domain)}{$service.domain}{else}#{$service.id}{/if} - The package has been changed to {$product.name}.</p>', 'Dear {$client.firstname} {$client.lastname},\r\n\r\nService: {if !empty($service.domain)}{$service.domain}{else}#{$service.id}{/if} - The package has been changed to {$product.name}.', 1, 2, 1, 0),
('Account:ChangePackage:Success', 'Product', 'Admin', 1, 'The package change for the service was successful', '<h1>Account package changed</h1>\r\n\r\n<p>\r\n<b>Account: </b> {$service.id}<br>\r\n<b>Client: </b>  {$client.firstname} {$client.lastname}<br>\r\n<br>\r\n<b>Server Name:</b>  {$server.name}<br>\r\n<b>New Product:</b>  {$product.name}<br>\r\n<b>Username:</b>  {$service.username}<br>\r\n</p>', 'Account package changed\r\n\r\nAccount: {$service.id}\r\nClient: {$client.firstname} {$client.lastname}\r\n\r\nServer Name: {$server.name}\r\nNew Product: {$product.name}\r\nUsername: {$service.username}', 1, 2, 1, 0),
('Account:ChangePackage:Failed', 'Product', 'Admin', 1, 'The package change for the service has failed', '<h1>Account package change failed</h1>\r\n\r\n<p>\r\n<b>Account: </b> {$service.id}<br>\r\n<b>Client: </b>  {$client.firstname} {$client.lastname}<br>\r\n<br>\r\n<b>Server Name:</b>  {$server.name}<br>\r\n<b>New Product:</b>  {$product.name}<br>\r\n<b>Username:</b>  {$service.username}<br>\r\n<br><br>\r\n\r\n<b>Error:</b> {$error}</p>', 'Account package change failed\r\n\r\nAccount: {$service.id}\r\nClient: {$client.firstname} {$client.lastname}\r\n\r\nServer Name: {$server.name}\r\nNew Product: {$product.name}\r\nUsername: {$service.username}\r\n\r\nError: {$error}', 1, 2, 1, 0);